import React from 'react';
import {StyleSheet, Text, View, Dimensions, Image} from 'react-native';
import Carousel from 'react-native-snap-carousel';

const dimensions = Dimensions.get("window");

export default function Simulations() {
  const carouselRef = React.useRef();

  const carouselItems = [
    {
        title:"Bitcoin",
        text: "Text 1",
        icon: 'https://cryptoicons.org/api/icon/btc/1000'
    },
    {
        title:"Ethereum",
        text: "Text 2",
        icon: 'https://cryptoicons.org/api/icon/eth/1000'
    },
    {
        title:"Litecoin",
        text: "Text 3",
        icon: 'https://cryptoicons.org/api/icon/ltc/1000'
    },
    {
        title:"Caradano",
        text: "Text 4",
        icon: 'https://cryptoicons.org/api/icon/ada/1000'
    },
    {
      title:"Stellar",
      text: "Text 5",
      icon: 'https://cryptoicons.org/api/icon/xlm/1000'
  }
  ]

  const renderItem = ({item, index}) => {
    return (
      <View style={styles.predictionCard}>
        <View style={styles.titleContainer}>
          <Image style={styles.tinyLogo} source={{uri: item.icon}}/>
          <Text style={[styles.text, {marginLeft: 10, marginTop: 5}]}>{item.title}</Text>
        </View>
      </View>
    );
  };


  return (
    <View style={{flex: 1, backgroundColor: '#e9f0ff'}}>
      <View style={styles.topBar}>
          <Text style={[styles.text, {fontSize: 18}]}>CryptoSimulator</Text>
      </View>
      <View style={styles.container}>
      <Carousel
          ref={carouselRef}
          data={carouselItems}
          renderItem={renderItem}
          sliderWidth={dimensions.width}
          itemWidth={dimensions.width}
        />
      </View>
    </View>
/*     <View style={styles.container}>
      <View>
        <Carousel
          ref={carouselRef}
          data={carouselItems}
          renderItem={renderItem}
          sliderWidth={dimensions.width}
          itemWidth={dimensions.width}
          style={{justifyContent: 'center', backgroundColor: '#e9f0ff'}}
        />
      </View>
    </View> */
);
}

const styles = StyleSheet.create({ 
  container: {
    flex: 1,
    marginTop: 70,
  },
  topBar: {
    position: 'absolute',
    zIndex: 200,
    top: 0,
    height: 60,
    justifyContent: 'flex-end',
    alignItems: 'center',
    width: '100%',
    flex: 1,
    backgroundColor: "#fff",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 1,
    },
    shadowOpacity: 0.1,
    shadowRadius: 5,
    paddingBottom: 8
  },
  text: {
    fontFamily: 'Poppins_600SemiBold',
    fontSize: 30,
    color: '#000000'
  },
  titleContainer: {
    width: '100%',
    flexDirection: 'row',
    alignItems: 'flex-start'
  },
  tinyLogo: {
    width: 50,
    height: 50,
  },
  predictionCard:{
    backgroundColor: '#e9f0ff',
    padding: 30,
    height: '100%'
  },
  text: {
    fontFamily: 'Poppins_600SemiBold',
    fontSize: 30,
    color: '#000000'
  }
})
