    else if(index == 3){
      var end = (currentDate - 365*24*60*60*1000);
      newData = allData.filter(sample => sample[0] >= end);
    }
    else if(index == 4){
      newData = allData;
    }
    setData(newData);
  }


  return (
    <View>
      <View style={{flex: 1, textAlign: 'left'}}>
        <Text style={styles.cryptoSymbol}>{props.symbol}</Text>
        <Text style={styles.cryptoName}>{props.name}</Text>
      </View>

      <View style={{flex: 1, height: 20, textAlign: 'center', marginBottom: 20}}>
        <PriceViewer touchX={touchX} deviceWidth={width} data={data}></PriceViewer>
      </View>

      <View style={{ height: 80, flexDirection: 'row', marginBottom: 15, marginTop: 15 }}>
        <View style={{ flex: 1}} {...panResponder.panHandlers}>
          <AreaChart
            style={{ flex:1, height: 80}}
            data={data.map(x => x[1])}
            contentInset={{ top: 10, bottom: 10 }}
            curve={shape.curveNatural}
            svg={{ fill: 'url(#gradient)' }}
            >
            <Tooltip touchX={touchX} deviceWidth={width} data={data}/>
            <ChartLine/>
            <Gradient/>
          </AreaChart>
        </View>
      </View>

      <View style={{flex: 1, alignItems: 'center'}}>
        <View style={styles.buttonsContainer}>
          <View style={period[0] == 1 ? [styles.button, {backgroundColor: 'rgb(41, 114, 255)'}]:styles.button} onStartShouldSetResponder={() => buttonCLicked(0)}>
            <Text style={period[0] == 1 ? [styles.buttonText, {color: '#ffffff'}]:styles.buttonText}>
              1W
            </Text>
          </View>
          <View style={period[1] == 1 ? [styles.button, {backgroundColor: 'rgb(41, 114, 255)'}]:styles.button} onStartShouldSetResponder={() => buttonCLicked(1)}>
            <Text style={period[1] == 1 ? [styles.buttonText, {color: '#ffffff'}]:styles.buttonText}>
              1M
            </Text>
          </View>
          <View style={period[2] == 1 ? [styles.button, {backgroundColor: 'rgb(41, 114, 255)'}]:styles.button} onStartShouldSetResponder={() => buttonCLicked(2)}>
            <Text style={period[2] == 1 ? [styles.buttonText, {color: '#ffffff'}]:styles.buttonText}>
              3M
            </Text>
          </View>
          <View style={period[3] == 1 ? [styles.button, {backgroundColor: 'rgb(41, 114, 255)'}]:styles.button} onStartShouldSetResponder={() => buttonCLicked(3)}>
            <Text style={period[3] == 1 ? [styles.buttonText, {color: '#ffffff'}]:styles.buttonText}>
              1Y
            </Text>
          </View>
          <View style={period[4] == 1 ? [styles.button, {backgroundColor: 'rgb(41, 114, 255)'}]:styles.button} onStartShouldSetResponder={() => buttonCLicked(4)}>
            <Text style={period[4] == 1 ? [styles.buttonText, {color: '#ffffff'}]:styles.buttonText}>
              3Y
            </Text>
          </View>
        </View>
      </View>
    </View>
  );
};


