import { View, PanResponder } from 'react-native';
import React from 'react';
import 'react-native-gesture-handler';

export default function TouchHandler(props){
    const data = props.data;

    const panResponder = React.useRef(
        PanResponder.create({
          onStartShouldSetPanResponder: () => true,
          onPanResponderGrant: (evt) => {
            if(evt.nativeEvent.locationX >= 0 && evt.nativeEvent.locationX <= evt.currentTarget.clientWidth){
              var index = Math.floor((evt.nativeEvent.locationX / evt.currentTarget.clientWidth) * data.length);
              props.setIndex(index)
              setSwipeIndex(index);
            }
          },
          onPanResponderMove: (evt) => {
            if(evt.nativeEvent.locationX >= 0 && evt.nativeEvent.locationX <= evt.currentTarget.clientWidth){
              var index = Math.floor((evt.nativeEvent.locationX / evt.currentTarget.clientWidth) * data.length);
              setSwipeIndex(index);
            }
          },
          onPanResponderRelease: () => {
            setSwipeIndex(-1);
          }
        })
    ).current;


    return(
        <View style={{ flex: 1}} {...panResponder.panHandlers}/>
    )
};
