import { StatusBar } from 'expo-status-bar';
import 'react-native-gesture-handler';
import React, {useState, useEffect} from 'react';
import { StyleSheet, Text, View, SafeAreaView, TouchableOpacity} from 'react-native';
import { useFonts, Poppins_300Light, Poppins_400Regular, Poppins_600SemiBold} from '@expo-google-fonts/poppins';
import { AppLoading } from 'expo';
import Autocomplete from 'react-native-autocomplete-input';

export default function Statistics() {
  let [fontsLoaded] = useFonts({
    Poppins_300Light,
    Poppins_400Regular,
    Poppins_600SemiBold
  });

// For Main Data
const [films, setFilms] = useState([]);

// For Filtered Data
const [filteredFilms, setFilteredFilms] = useState([]);

// For Selected Data
const [selectedValue, setSelectedValue] = useState({});

useEffect(() => {
  fetch('https://aboutreact.herokuapp.com/getpost.php?offset=1')
    .then((res) => res.json())
    .then((json) => {
      const {results: films} = json;
      setFilms(films);
      //setting the data in the films state
    })
    .catch((e) => {
      alert(e);
    });
}, []);

const findFilm = (query) => {
  // Method called every time when we change the value of the input
  if (query) {
    // Making a case insensitive regular expression
    const regex = new RegExp(`${query.trim()}`, 'i');
    // Setting the filtered film array according the query
    setFilteredFilms(
        films.filter((film) => film.title.search(regex) >= 0)
    );
  } else {
    // If the query is null then return blank
    setFilteredFilms([]);
  }
};

return (
  <SafeAreaView style={{flex: 1, backgroundColor:'#000'}}>
    <View style={styles.titleWrapper}>
        <Text style={styles.text}>
            Predict the future of your cryptos
        </Text>
    </View>
    <View style={styles.container}>
      <Autocomplete
      inputContainerStyle={styles.inputContainer}
      style={styles.autocomplete}
        autoCapitalize="none"
        autoCorrect={false}// da non mettere a true, è il correttore automatico della tastiera
        containerStyle={styles.autocompleteContainer}
        // Data to show in suggestion
        data={filteredFilms}
        // Default value if you want to set something in input
        defaultValue={
          JSON.stringify(selectedValue) === '{}' ?
          '' :
          selectedValue.title
        }
        // Onchange of the text changing the state of the query
        // Which will trigger the findFilm method
        // To show the suggestions
        onChangeText={(text) => findFilm(text)}
        placeholder="Enter the film title"
        renderItem={({item}) => (
          // For the suggestion view
          <TouchableOpacity
            style={styles.searchContainer}
            onPress={() => {
              setSelectedValue(item);
              setFilteredFilms([]);
            }}>
            <Text style={styles.itemText}>
                {item.title}
            </Text>
          </TouchableOpacity>
        )}
      />
      <View style={styles.descriptionContainer}>
        {films.length > 0 ? (
          <>
            <Text style={styles.infoText}>
                 Selected Data
            </Text>
            <Text style={styles.infoText}>
              {JSON.stringify(selectedValue)}
            </Text>
          </>
        ) : (
          <Text style={styles.infoText}>
              Enter The Film Title
          </Text>
        )}
      </View>
    </View>
  </SafeAreaView>
);
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#000',
    alignItems: 'center',
    justifyContent: 'center',
  },
  titleWrapper:{
    height:'10%',
    alignItems:'center',
    justifyContent:'center',
  },

  text: {
    fontFamily: 'Poppins_600SemiBold',
    fontSize: 30,
    color: '#ffffff'
  },
  autocomplete:{
    height:40,
    color:'#fff',
  },
  autocompleteContainer: {
    backgroundColor: '#111111',
    width:'40%',
    height:40,
    color:'#fff',
    borderWidth: 0,
    borderRadius:10,
  },
  inputContainer:{
    backgroundColor:'#111111',
    borderRadius:20,
    width:'100%',
    height:40,
    color:'#fff',
    alignItems:'center',
    justifyContent:'center',
  },
  searchContainer:{
    backgroundColor: '#111111',
    width:'100%'
  },
  descriptionContainer: {
    flex: 1,
    justifyContent: 'center',
  },
  itemText: {
    textAlign:'center',
    backgroundColor: '#111111',
    fontSize: 20,
    color:'#fff',
    paddingTop: 10,
    paddingBottom: 10,
    margin: 5,
  },
  infoText: {
    textAlign: 'center',
    fontSize: 16,
    color:'#fff',
  },
});
