import { StatusBar } from 'expo-status-bar';
import 'react-native-gesture-handler';
import React from 'react';
import { StyleSheet, Text, View, SafeAreaView,Dimensions} from 'react-native';
import { useFonts, Poppins_300Light, Poppins_400Regular, Poppins_600SemiBold} from '@expo-google-fonts/poppins';
import { AppLoading } from 'expo';
import {PieChart} from 'react-native-chart-kit';
import Icon from 'react-native-vector-icons/FontAwesome';


export default function Account() {
  let [fontsLoaded] = useFonts({
    Poppins_300Light,
    Poppins_400Regular,
    Poppins_600SemiBold
  });

  const data = [
    { name: 'Bitcoin', wallet: 21500000, color: '#00f', legendFontColor: '#ffffff', legendFontSize: 15 },
    { name: 'FrancescoBros', wallet: 8538000, color: 'rgba(131, 167, 234, 1)', legendFontColor: '#ffffff', legendFontSize: 15 },
    { name: 'Ethereum', wallet: 2800000, color: '#4ddbff', legendFontColor: '#ffffff', legendFontSize: 15 },
    { name: 'Libra', wallet: 527612, color: '#ffffff', legendFontColor: '#ffffff', legendFontSize: 15 },

  ]


  if (!fontsLoaded) {
    return <AppLoading/>;
  }
  else{
    return (
        <SafeAreaView style={styles.container}>
            <View style={styles.header}>
            <Icon name="edit" size={30} style={styles.modifyIcon} />
            <Text style={styles.headerTopText}>
                welcome, Amedeo
            </Text>
            <Text style={styles.headerText}>
                Your smart wallet!
            </Text>
            </View>
            <View style={styles.chartWrapper}>
            <PieChart
              data={data}
              width={Dimensions.get('window').width}
              height={Dimensions.get('window').height*0.3}
              chartConfig={{
                backgroundColor: '#e26a00',
                backgroundGradientFrom: '#fb8c00',
                backgroundGradientTo: '#ffa726',
                decimalPlaces: 2, 
                color: (opacity = 1) => `rgba(255, 255, 255, ${opacity})`,
                style: {
                  borderRadius: 16
                }
              }}
              accessor="wallet"
              backgroundColor="transparent"
              paddingLeft="15"
            />
            </View>
            <View style={styles.bottom}>
            <View style={styles.box}>
            <Text style={styles.boxText}>
                Invested
            </Text>
            <Text style={styles.boxValueText}>
              10000â‚¬
            </Text>
            </View>
            <View style={styles.box}>
            <Text style={styles.boxText}>
                Return
            </Text>
            <Text style={styles.boxValueText}>
                1057.65â‚¬
            </Text>
            </View>
            </View>
        </SafeAreaView>
    );
  }
};

const styles = StyleSheet.create({
  container: {
    flex:1,
    flexDirection:'column',
  backgroundColor: '#000000',
  alignItems: 'center',
  justifyContent: 'center',
  },
  headerTopText: {
    fontFamily: 'Poppins_600SemiBold',
    fontSize: 15,
    color: '#4ddbff',
    position:'absolute',
    bottom:'30%',
    left:'5%'
  },
  headerText: {
    fontFamily: 'Poppins_600SemiBold',
    fontSize: 30,
    color: '#ffffff',
    position:'absolute',
    bottom:'10%',
    left:'5%'
  },
  header:{
    backgroundColor: '#111111',
    height:'20%',
    width:'100%',
    shadowColor: '#666',
   shadowOffset: { width: 2, height: 2 },
   shadowOpacity: 0.5,
   shadowRadius: 2,
   elevation: 2,
  },
  chartWrapper:{
    height:'40%',
    width:'100%',
    shadowColor: '#666',
   shadowOffset: { width: 2, height: 2 },
   shadowOpacity: 0.5,
   shadowRadius: 2,
   elevation: 2,
  },
  bottom:{
    height:'40%',
    width:'100%',
    flexDirection:'row',
    flex:1,
  },
  box:{
    width:'40%',
    height:'60%',
    flex:1,
    flexDirection:'column',
    borderRadius:20,
    margin:10,
  backgroundColor: '#111111',
  alignItems: 'center',
  justifyContent: 'center',
  shadowColor: '#666',
   shadowOffset: { width: 2, height: 2 },
   shadowOpacity: 0.5,
   shadowRadius: 2,
   elevation: 2,
  },
  boxText:{
    fontFamily: 'Poppins_600SemiBold',
    fontSize: 20,
    color: '#4ddbff',
    position:'absolute',
    top:'10%',
    left:'5%'
  },
  boxValueText:{
    fontFamily: 'Poppins_600SemiBold',
    fontSize: 40,
    color: '#ffffff',
  },
  modifyIcon:{
    color:'#4ddbff',
    position:'absolute',
    top:'5%',
    right:'5%',
  }
});
