import React, {useState} from 'react';
import {StyleSheet, Text, View, Dimensions, Image} from 'react-native';
import Carousel, {Pagination} from 'react-native-snap-carousel';

const dimensions = Dimensions.get("window");

export default function Simulations() {
  const carouselRef = React.useRef();
  const [activeSlide, setActiveSlide] = useState(0);

  const carouselItems = [
    {
        title:"Bitcoin",
        text: "Text 1",
        icon: 'https://cryptoicons.org/api/icon/btc/1000'
    },
    {
        title:"Ethereum",
        text: "Text 2",
        icon: 'https://cryptoicons.org/api/icon/eth/1000'
    },
    {
        title:"Litecoin",
        text: "Text 3",
        icon: 'https://cryptoicons.org/api/icon/ltc/1000'
    },
    {
        title:"Caradano",
        text: "Text 4",
        icon: 'https://cryptoicons.org/api/icon/ada/1000'
    },
    {
      title:"Stellar",
      text: "Text 5",
      icon: 'https://cryptoicons.org/api/icon/xlm/1000'
  }
  ]

  const renderItem = ({item, index}) => {
    return (
      <View style={styles.predictionCard}>
        <View style={{width: 54, height: 54, paddingTop: 1.5, borderWidth: 2, borderColor: 'rgb(41, 114, 255)', borderRadius: '50%'}}>
            <Image style={styles.tinyLogo} source={{uri: item.icon}}/>
        </View>
        <Text style={[styles.text, {marginTop: 5, fontSize: 20, fontFamily: 'Poppins_500Medium'}]}>{item.title}</Text>
      </View>
    );
  };


  const Pages = () =>{
    return(
      <Pagination
        dotsLength={carouselItems.length}
        activeDotIndex={activeSlide}
        dotStyle={{
            width: 5,
            height: 5,
            borderRadius: 5,
            marginHorizontal: -5,
            backgroundColor: 'rgba(41, 114, 255, 0.92)'
        }}
        inactiveDotStyle={{
            backgroundColor: 'rgba(0, 0, 0, 0.92)'
        }}
        inactiveDotOpacity={0.4}
        inactiveDotScale={0.6}
      />
    );
  }


  return (
    <View style={{flex: 1, backgroundColor: '#e9f0ff'}}>
      <View style={styles.topBar}>
          <Text style={[styles.text, {fontSize: 18, fontFamily: 'Poppins_600SemiBold'}]}>CryptoSimulator</Text>
      </View>
      <View style={styles.container}>
      <Pages/>
      <Carousel
          ref={carouselRef}
          data={carouselItems}
          renderItem={renderItem}
          sliderWidth={dimensions.width}
          itemWidth={dimensions.width}
          onSnapToItem={(index) => setActiveSlide(index)}
        />
      </View>
    </View>
);
}

const styles = StyleSheet.create({ 
  container: {
    flex: 1,
    marginTop: 40,
  },
  topBar: {
    position: 'absolute',
    zIndex: 200,
    top: 0,
    height: 60,
    justifyContent: 'flex-end',
    alignItems: 'center',
    width: '100%',
    flex: 1,
    backgroundColor: "#e9f0ff",
    shadowColor: "#000",
    paddingBottom: 8,
  },
  text: {
    fontFamily: 'Poppins_600SemiBold',
    fontSize: 30,
    color: '#000000'
  },
  titleContainer: {
    width: '100%',
    flexDirection: 'row',
    alignItems: 'flex-start'
  },
  tinyLogo: {
    width: 50,
    height: 50,
  },
  predictionCard:{
    flex:1,
    alignItems: 'center',
    backgroundColor: '#e9f0ff',
    padding: 20,
    paddingTop: 0
  },
  text: {
    color: '#000000'
  }
})
