import {StyleSheet, Text, View} from 'react-native';
import React from 'react';
import { useFonts, Poppins_300Light, Poppins_400Regular, Poppins_500Medium, Poppins_600SemiBold} from '@expo-google-fonts/poppins';

export default function PriceViewer(props){
    const swipeIndex = props.swipeIndex;
    const data = props.data;
    let [fontsLoaded] = useFonts({
        Poppins_300Light,
        Poppins_400Regular,
        Poppins_500Medium,
        Poppins_600SemiBold
    });

    if (!fontsLoaded) {
        return(null);
    }
    else{
        if(swipeIndex >= 0 && swipeIndex <= data.length){
            return(
                <View>
                    <Text style={styles.currentPrice}>{parsePrice(data[swipeIndex][1])}</Text>
                    <Text style={styles.currentDate}>{getDate(data[swipeIndex][0])}
                    </Text>
                </View>
            )
        }
        else{
            return(null);
        }
    }
};

const styles = StyleSheet.create({
    currentPrice: {
        fontFamily: 'Poppins_500Medium',
        fontSize: 18,
        color: '#000000',
        margin: 0
    },
    currentDate: {
        fontFamily: 'Poppins_500Medium',
        fontSize: 12,
        color: '#000000',
        margin: 0
    },
})
