  )

  useEffect(()=>{
    if(loading && !fontsLoaded){
    firebase.functions().httpsCallable('getCryptoData')({asset: 'bitcoin', start: '2020-01-01', end: '2020-02-01', interval: '1d'}).then(response =>{
      if(response.data.statusCode == 200){
        setCryptoData(response.data.body);
        setLoading(false);
      }
      else{
        console.error(response.data.body);
      }
    }).catch(error =>{
      console.error(error);
    })
  }},[])

  if (!fontsLoaded) {
    return <AppLoading/>;
  }
  else{
    if(!loading){
      let data = [];
      (cryptoData.values).forEach(sample =>{
        data.push(sample[4]);
      })

      const CustomGrid = ({ x, y, data, ticks }) => (
        <G>
            {
                ticks.map(tick => (
                    <Line
                        key={ tick }
                        x1={ '0%' }
                        x2={ '100%' }
                        y1={ y(tick) }
                        y2={ y(tick) }
                        stroke={ 'rgba(0,0,0,0.2)' }
                    />
                ))
            }
            {
                data.map((_, index) => (
                    <Line
                        key={ index }
                        y1={ '0%' }
                        y2={ '100%' }
                        x1={ x(index) }
                        x2={ x(index) }
                        stroke={ 'rgba(0,0,0,0.2)' }
                    />
                ))
            }
        </G>
    )

    return (
      <View style={styles.container}>
        <View style={styles.chartContainer}>
          <Text style={styles.cryptoSymbol}>{cryptoData.symbol}</Text>
          <Text style={styles.cryptoName}>{cryptoData.name}</Text>

          <View style={{ height: 200, flexDirection: 'row', marginBottom: 15, marginTop: 15 }}>
              <YAxis
                  data={data}
                  contentInset={{ top: 10, bottom: 10 }}
                  svg={axesSvg}
                  numberOfTicks={ 6 }
              />
              <View style={{ flex: 1, marginLeft: 10 }}>
                <AreaChart
                  style={{ height: 200 }}
                  data={data}
                  contentInset={{ top: 10, bottom: 10 }}
                  curve={shape.curveNatural}
                  svg={{ fill: 'url(#gradient)' }}
                  >
                  <CustomGrid/>
                  <Line/>
                  <Gradient/>
                </AreaChart>
                  <XAxis
                      style={{ marginHorizontal: -10, marginTop: 5}}
                      data={data}
                      formatLabel={(value, index) => index + 1 }
                      contentInset={{ left: 10, right: 10 }}
                      svg={axesSvg}
                      numberOfTicks={ 15 }
                  />
              </View>
          </View>

          <View style={styles.buttonsContainer}>
            <View style={styles.button}>
              <Text style={styles.buttonText}>
                Week
              </Text>
            </View>
            <View style={styles.button}>
              <Text style={styles.buttonText}>
                Month
              </Text>
            </View>
            <View style={styles.button}>
              <Text style={styles.buttonText}>
                Year
              </Text>
            </View>
          </View>
        </View>
      </View>
    );
    }
    else{
      return(
        <View style={styles.container}>
          <Text style={styles.text}>
              LOADING...
          </Text>
        </View>
      );
    }
  }
};

