class Chart extends React.PureComponent {

    render() {

        const data = [ 50, 10, 40, 95, -4, -24, 85, 91, 35, 53, -53, 24, 50, -20, -80 ]

        const CustomGrid = ({ x, y, data, ticks }) => (
            <G>
                {
                    // Horizontal grid
                    ticks.map(tick => (
                        <Line
                            key={ tick }
                            x1={ '0%' }
                            x2={ '100%' }
                            y1={ y(tick) }
                            y2={ y(tick) }
                            stroke={ 'rgba(0,0,0,0.2)'
                          }
                        />
                    ))
                }
                {
                    // Vertical grid
                    data.map((_, index) => (
                        <Line
                            key={ index }
                            y1={ '0%' }
                            y2={ '100%' }
                            x1={ x(index) }
                            x2={ x(index) }
                            stroke={ 'rgba(0,0,0,0.1)' }
                        />
                    ))
                }
            </G>
        )

        return (
          <View style={{ height: 200, flexDirection: 'row', marginBottom: 15, marginTop: 15 }}>
              <View style={{ flex: 1, marginLeft: 10 }}>
                <AreaChart
                  style={{ height: 200 }}
                  data={data}
                  contentInset={{ top: 10, bottom: 10 }}
                  curve={shape.curveNatural}
                  svg={{ fill: 'url(#gradient)' }}
                  >
                  <CustomGrid/>
                  <Line/>
                  <Gradient/>
                </AreaChart>
              </View>
          </View>
        )
    }

}

export default Chart
