import 'react-native-gesture-handler';
import { Animated, StyleSheet, Text, ScrollView, View, PanResponder, Dimensions, SafeAreaView} from 'react-native';
import React, {useState,useEffect,useRef} from 'react';
import { useFonts, Poppins_300Light, Poppins_400Regular, Poppins_500Medium, Poppins_600SemiBold} from '@expo-google-fonts/poppins';
import { AppLoading } from 'expo';
import * as firebase from 'firebase'
import "firebase/functions";
import { AreaChart} from 'react-native-svg-charts'
import * as shape from 'd3-shape'
import { Defs, LinearGradient, Stop, Path} from 'react-native-svg'
import Tooltip from './Tooltip'
import PriceViewer from './PriceViewer'
import Icon from 'react-native-vector-icons/FontAwesome5'


export default function Predictions() {
  const [loading, setLoading] = useState(true);
  const [cryptoData, setCryptoData] = useState([]);
  const [scrollable, setScrollable] = useState(true);
  let [fontsLoaded] = useFonts({
    Poppins_300Light,
    Poppins_400Regular,
    Poppins_500Medium,
    Poppins_600SemiBold
  });

  const setScroll = (scroll) => {
    setScrollable(scroll);
  }

  var endDate = new Date();
  endDate = endDate.getFullYear() + "-" + (endDate.getMonth() + 1) + "-" + endDate.getDate();

  var startDate = new Date((new Date()).getTime() - 3*365*24*60*60*1000);
  startDate = startDate.getFullYear() + "-" + (startDate.getMonth() + 1) + "-" + startDate.getDate();

  useEffect(()=>{
    if(loading && !fontsLoaded){
    firebase.functions().httpsCallable('getCryptoData')({assets: ['BTC', 'ETH', 'RPC'], start: startDate, end: endDate, interval: '1d'}).then(response =>{
      if(response.data.statusCode == 200){
        setCryptoData(response.data.body);
        setLoading(false);
      }
      else{
        console.error(response.data.body);
      }
    }).catch(error =>{
      console.error(error);
    })
  }},[])

  if (!fontsLoaded) {
    return <AppLoading/>;
  }
  else{
    if(!loading){
    return (
      <SafeAreaView style={{flex: 1, backgroundColor:'#e9f0ff'}}>
        <ScrollView contentContainerStyle={styles.container} showsVerticalScrollIndicator={false} scrollEnabled={scrollable}>
          {cryptoData.map((crypto)=><Chart name={crypto.name} symbol={crypto.symbol} scroll={setScroll} data={(crypto.values).map(x => [x[0], x[4]])}></Chart>)}
          <View style={styles.addChart}></View>
        </ScrollView>
      </SafeAreaView>
    );
    }
    else{
      return(
        <View style={styles.container}>
          <Text style={styles.text}>
              LOADING...
          </Text>
        </View>
      );
    }
  }
};

const Chart = (props) => {
  const allData = props.data;
  const [data, setData] = useState(allData.filter(sample => sample[0] >= ((new Date).getTime() - 30*24*60*60*1000)));
  const [period, setPeriod] = useState([0,1,0,0,0]);
  const [touchX, setTouchX] = useState(-1);
  const width = Math.ceil(0.92 * Dimensions.get('window').width - 40);
  const pan = useRef(new Animated.ValueXY()).current;

  const ChartLine = ({ line }) => (
    <Path
        key={'line'}
        d={line}
        stroke={'rgb(41, 114, 255)'}
        fill={'none'}
        strokeWidth={2}
    />
  )

  const Gradient = ({ index }) => (
    <Defs key={index}>
        <LinearGradient id={'gradient'} x1={'0%'} y1={'0%'} x2={'0%'} y2={'100%'}>
            <Stop offset={'0%'} stopColor={'rgb(41, 114, 255)'} stopOpacity={0.2}/>
            <Stop offset={'50%'} stopColor={'rgb(41, 114, 255)'} stopOpacity={0}/>
        </LinearGradient>
    </Defs>
  )


  const panResponder = React.useRef(
    PanResponder.create({
      onStartShouldSetPanResponder: () => true,
      onPanResponderGrant: (evt) => {
        props.scroll(false);
        if(evt.nativeEvent.locationX >= 0 && evt.nativeEvent.locationX <= width){
          setTouchX(evt.nativeEvent.locationX);
        }
      },
      onPanResponderMove: (evt) => {
        if(evt.nativeEvent.locationX >= 0 && evt.nativeEvent.locationX <= width){
          setTouchX(evt.nativeEvent.locationX);
        }
      },
      onPanResponderRelease: () => {
        props.scroll(true);
        setTouchX(-1);
      },
      onPanResponderTerminate: () => {
        props.scroll(true);
        setTouchX(-1);
      }
    })
  ).current;


  const Drag = React.useRef(
    PanResponder.create({
      onStartShouldSetPanResponder: () => true,
      onPanResponderGrant: (evt) => {
        console.log("entras");
      },
      onPanResponderMove: Animated.event(
        [
          null,
          { dx: pan.x, dy: pan.y }
        ]
      ),

      onPanResponderRelease: () => {
        pan.flattenOffset();
      },
      onPanResponderTerminate: () => {
        pan.flattenOffset();
      }
    })
  ).current;

  function buttonCLicked(index){
    var newPeriod = [0,0,0,0,0];
    newPeriod[index] = 1;
    setPeriod(newPeriod);
    var currentDate = (new Date()).getTime();
    var newData;

    if(index == 0){
      var end = (currentDate - 7*24*60*60*1000);
      newData = allData.filter(sample => sample[0] >= end);
    }
    else if(index == 1){
      var end = (currentDate - 30*24*60*60*1000);
      newData = allData.filter(sample => sample[0] >= end);
    }
    else if(index == 2){
      var end = (currentDate - 90*24*60*60*1000);
      newData = allData.filter(sample => sample[0] >= end);
    }
    else if(index == 3){
      var end = (currentDate - 365*24*60*60*1000);
      newData = allData.filter(sample => sample[0] >= end);
    }
    else if(index == 4){
      newData = allData;
    }
    setData(newData);
  }


  return (
    <Animated.View style={{transform:[{ translateX: pan.x }, { translateY: pan.y}]}, styles.chartContainer}>
      <View style={{flex: 1, textAlign: 'left'}}>
        <View style={{flex: 1, flexDirection: 'row', justifyContent: 'space-between'}}>
          <Text style={styles.cryptoSymbol}>{props.symbol}</Text>
          <View style={{width: 40}} {...Drag.panHandlers}>
            <Icon name="grip-lines" size={20} color="#c0c0c7" />
          </View>
        </View>
        <Text style={styles.cryptoName}>{props.name}</Text>
      </View>

      <View style={{flex: 1, height: 20, textAlign: 'center', marginBottom: 20}}>
        <PriceViewer touchX={touchX} deviceWidth={width} data={data}></PriceViewer>
      </View>

      <View style={{ height: 80, flexDirection: 'row', marginBottom: 15, marginTop: 15 }}>
        <View style={{ flex: 1}} {...panResponder.panHandlers}>
          <AreaChart
            style={{ flex:1, height: 80}}
            data={data.map(x => x[1])}
            contentInset={{ top: 10, bottom: 10 }}
            curve={shape.curveNatural}
            svg={{ fill: 'url(#gradient)' }}
            >
            <Tooltip touchX={touchX} deviceWidth={width} data={data}/>
            <ChartLine/>
            <Gradient/>
          </AreaChart>
        </View>
      </View>

      <View style={{flex: 1, alignItems: 'center'}}>
        <View style={styles.buttonsContainer}>
          <View style={period[0] == 1 ? [styles.button, {backgroundColor: 'rgb(41, 114, 255)'}]:styles.button} onStartShouldSetResponder={() => buttonCLicked(0)}>
            <Text style={period[0] == 1 ? [styles.buttonText, {color: '#ffffff'}]:styles.buttonText}>
              1W
            </Text>
          </View>
          <View style={period[1] == 1 ? [styles.button, {backgroundColor: 'rgb(41, 114, 255)'}]:styles.button} onStartShouldSetResponder={() => buttonCLicked(1)}>
            <Text style={period[1] == 1 ? [styles.buttonText, {color: '#ffffff'}]:styles.buttonText}>
              1M
            </Text>
          </View>
          <View style={period[2] == 1 ? [styles.button, {backgroundColor: 'rgb(41, 114, 255)'}]:styles.button} onStartShouldSetResponder={() => buttonCLicked(2)}>
            <Text style={period[2] == 1 ? [styles.buttonText, {color: '#ffffff'}]:styles.buttonText}>
              3M
            </Text>
          </View>
          <View style={period[3] == 1 ? [styles.button, {backgroundColor: 'rgb(41, 114, 255)'}]:styles.button} onStartShouldSetResponder={() => buttonCLicked(3)}>
            <Text style={period[3] == 1 ? [styles.buttonText, {color: '#ffffff'}]:styles.buttonText}>
              1Y
            </Text>
          </View>
          <View style={period[4] == 1 ? [styles.button, {backgroundColor: 'rgb(41, 114, 255)'}]:styles.button} onStartShouldSetResponder={() => buttonCLicked(4)}>
            <Text style={period[4] == 1 ? [styles.buttonText, {color: '#ffffff'}]:styles.buttonText}>
              3Y
            </Text>
          </View>
        </View>
      </View>
    </Animated.View>
  );
};


