import {StyleSheet, Text, View} from 'react-native';
import React from 'react';
import { useFonts, Poppins_300Light, Poppins_400Regular, Poppins_500Medium, Poppins_600SemiBold} from '@expo-google-fonts/poppins';

var days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
var months = ["Gen", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];

function getDate(timestamp){
    var date = new Date(timestamp);
    var textDate = days[date.getDay()] + " " + date.getDate() + " " + months[date.getMonth()] + " " + date.getFullYear();
    return textDate;
}


function parsePrice(price){
    price = price.toFixed(2);
    var integer = String(Math.trunc(price));
    var decimal = price.split(".")[1];
    if(integer.length > 3){
        integer = [integer.slice(0, (integer.length - 3)), ".", integer.slice((integer.length - 3))].join('');
    }
    return integer + "," + decimal + "$";
}


export default function PriceViewer(props){
    const swipeIndex = props.swipeIndex;
    const data = props.data;
    let [fontsLoaded] = useFonts({
        Poppins_300Light,
        Poppins_400Regular,
        Poppins_500Medium,
        Poppins_600SemiBold
    });

    if (!fontsLoaded) {
        return(null);
    }
    else{
        if(swipeIndex >= 0 && swipeIndex <= data.length){
            return(
                <View>
                    <Text style={styles.currentPrice}>{parsePrice(data[swipeIndex][1])}</Text>
                    <Text style={styles.currentDate}>{getDate(data[swipeIndex][0])}
                    </Text>
                </View>
            )
        }
        else{
            return(null);
        }
    }
};

const styles = StyleSheet.create({
    currentPrice: {
        fontFamily: 'Poppins_500Medium',
        fontSize: 18,
        color: '#000000',
        margin: 0
    },
    currentDate: {
        fontFamily: 'Poppins_500Medium',
        fontSize: 12,
        color: '#000000',
        margin: 0
    },
})
