import 'react-native-gesture-handler';
import { Animated, StyleSheet, Text, TouchableOpacity, ScrollView, FlatList, View, PanResponder, Dimensions, SafeAreaView} from 'react-native';
import React, {useState,useEffect,useRef,Component} from 'react';
import { useFonts, Poppins_300Light, Poppins_400Regular, Poppins_500Medium, Poppins_600SemiBold} from '@expo-google-fonts/poppins';
import { AppLoading } from 'expo';
import * as firebase from 'firebase'
import "firebase/functions";
import { AreaChart} from 'react-native-svg-charts'
import * as shape from 'd3-shape'
import { Defs, LinearGradient, Stop, Path} from 'react-native-svg'
import Tooltip from './Tooltip'
import PriceViewer from './PriceViewer'
import Icon from 'react-native-vector-icons/FontAwesome5'
import DraggableFlatList from 'react-native-draggable-dynamic-flatlist';


export default function Predictions() {
  const [loading, setLoading] = useState(true);
  const [cryptoData, setCryptoData] = useState([]);
  const [scrollable, setScrollable] = useState(true);
  const flatListRef = React.useRef();
  var scrollPosition = 0;
  let [fontsLoaded] = useFonts({
    Poppins_300Light,
    Poppins_400Regular,
    Poppins_500Medium,
    Poppins_600SemiBold
  });

  const setScroll = (scroll) => {
    setScrollable(scroll);
  }

  const moveItem = (old_index, new_index) => {
    var arr = cryptoData.slice();
    if (new_index >= arr.length) {
        var k = new_index - arr.length + 1;
        while (k--) {
            arr.push(undefined);
        }
    }
    arr.splice(new_index, 0, arr.splice(old_index, 1)[0]);
    setCryptoData(arr);
  };

  const scrollTo = (offset) => {
    flatListRef.current.scrollToOffset({ animated: true, offset: scrollPosition + offset });
  }

  const updateScrolPosition = (evt) =>{
    scrollPosition = evt.nativeEvent.contentOffset.y;
  }

  const renderItem = ({ item, index }) => {
    return (
      <Chart name={item.name} symbol={item.symbol} index={index} scrollTo={scrollTo} dataLength={cryptoData.length} scroll={setScroll} moveItem={moveItem} data={(item.values).map(x => [x[0], x[4]])}/>
    );
  };

  var endDate = new Date();
  endDate = endDate.getFullYear() + "-" + (endDate.getMonth() + 1) + "-" + endDate.getDate();

  var startDate = new Date((new Date()).getTime() - 3*365*24*60*60*1000);
  startDate = startDate.getFullYear() + "-" + (startDate.getMonth() + 1) + "-" + startDate.getDate();

  useEffect(()=>{
    if(loading && !fontsLoaded){
    firebase.functions().httpsCallable('getCryptoData')({assets: ['BTC', 'ETH', 'LTC', 'ADA', 'DOT', 'XLM'], start: startDate, end: endDate, interval: '1d'}).then(response =>{
      if(response.data.statusCode == 200){
        setCryptoData(response.data.body);
        setLoading(false);
      }
      else{
        console.error(response.data.body);
      }
    }).catch(error =>{
      console.error(error);
    })
  }},[])

  if (!fontsLoaded) {
    return <AppLoading/>;
  }
  else{
    if(!loading){
    return (
      <SafeAreaView style={{flex: 1, backgroundColor:'#e9f0ff'}}>
        <View style={styles.topBar}>
          <Text style={[styles.text, {fontSize: 18}]}>CryptoSimulator</Text>
        </View>
        <FlatList data={cryptoData} renderItem={renderItem} ref={flatListRef} contentContainerStyle={styles.container} onScroll={updateScrolPosition} showsVerticalScrollIndicator={false} scrollEnabled={scrollable}/>
      </SafeAreaView>
    );
    }
    else{
      return(
        <View style={{flex: 1, alignItems: 'center', justifyContent: 'center'}}>
          <View style={styles.topBar}>
            <Text style={[styles.text, {fontSize: 18}]}>CryptoSimulator</Text>
          </View>
          <Text style={styles.text}>
              LOADING...
          </Text>
        </View>
      );
    }
  }
};


const Chart = (props) => {
  const allData = props.data;
  const [data, setData] = useState(allData.filter(sample => sample[0] >= ((new Date).getTime() - 30*24*60*60*1000)));
  const [period, setPeriod] = useState([0,1,0,0,0]);
  const [touchX, setTouchX] = useState(-1);
  const width = Math.ceil(0.92 * Dimensions.get('window').width - 40);
  const pan = useRef(new Animated.ValueXY()).current;
  const [dragging, setDragging] = useState({elevation: 1, shadowRadius: 5, zIndex: 1});


  const ChartLine = ({ line }) => (
    <Path
        key={'line'}
        d={line}
        stroke={'rgb(41, 114, 255)'}
        fill={'none'}
        strokeWidth={2}
    />
  )

  const Gradient = ({ index }) => (
    <Defs key={index}>
        <LinearGradient id={'gradient'} x1={'0%'} y1={'0%'} x2={'0%'} y2={'100%'}>
            <Stop offset={'0%'} stopColor={'rgb(41, 114, 255)'} stopOpacity={0.2}/>
            <Stop offset={'50%'} stopColor={'rgb(41, 114, 255)'} stopOpacity={0}/>
        </LinearGradient>
    </Defs>
  )


  const panResponder = React.useRef(
    PanResponder.create({
      onStartShouldSetPanResponder: () => true,
      onPanResponderGrant: (evt) => {
        props.scroll(false);
        if(evt.nativeEvent.locationX >= 0 && evt.nativeEvent.locationX <= width){
          setTouchX(evt.nativeEvent.locationX);
        }
      },
      onPanResponderMove: (evt) => {
        if(evt.nativeEvent.locationX >= 0 && evt.nativeEvent.locationX <= width){
          setTouchX(evt.nativeEvent.locationX);
        }
      },
      onPanResponderRelease: () => {
        props.scroll(true);
        setTouchX(-1);
      },
      onPanResponderTerminate: () => {
        props.scroll(true);
        setTouchX(-1);
      }
    })
  ).current;


  function stopDragging(){
    let offset;
    if(pan.y._value / 301 < 0){
      offset = Math.floor(Math.abs(pan.y._value / 301)) * -1;
    }
    else{
      offset = Math.floor(pan.y._value / 301);
    }
    let newIndex;
    if(props.index == 0 && offset < 0){
      newIndex = props.index;
    }
    else if(props.index == props.dataLength - 1 && offset > 0){
      newIndex = props.index;
    }
    else{
      newIndex = props.index + offset;
      if(newIndex < 0){
        newIndex = 0;
      }
      else if(newIndex > props.dataLength - 1){
        newIndex = props.dataLength - 1;
      }
    }
    /*if(newIndex != props.index){
      props.moveItem(props.index , newIndex);
    }
    else{
      Animated.spring(
        pan,
        { toValue: { x: 0, y: 0 }, useNativeDriver: false }
      ).start();
    }*/
    Animated.spring(
      pan,
      { toValue: { x: 0, y: 0 }, useNativeDriver: false }
    ).start();
    props.scroll(true);
    setDragging({elevation: 100, shadowRadius: 5, zIndex: 100});
    setTimeout(function(){
      setDragging({elevation: 1, shadowRadius: 5, zIndex: 1});
    },300);
  }


  const Drag = React.useRef(
    PanResponder.create({
      onStartShouldSetPanResponder: () => true,
      onPanResponderGrant: () => {
        props.scroll(false);
        setDragging({elevation: 100, shadowRadius: 20, zIndex: 100});
        pan.setOffset({
          x: 0,
          y: 0
        });
      },
      onPanResponderMove: (evt, gestureState) => {
        if(Math.abs(pan.y._value) > 300){
          if(pan.y._value < 0){
            props.scrollTo(-1);
          }
          else{
            props.scrollTo(0);
          }
        }
        return Animated.event(
          [
            null, 
            { dx: pan.x, dy: pan.y }
          ],
          {useNativeDriver: false}
        )(evt, gestureState)
      },
      onPanResponderRelease: () => {
        stopDragging();
      },
      onPanResponderTerminate: () => {
        stopDragging();
      }
    })
  ).current;

  function buttonCLicked(index){
    var newPeriod = [0,0,0,0,0];
    newPeriod[index] = 1;
    setPeriod(newPeriod);
    var currentDate = (new Date()).getTime();
    var newData;

    if(index == 0){
      var end = (currentDate - 7*24*60*60*1000);
      newData = allData.filter(sample => sample[0] >= end);
    }
    else if(index == 1){
      var end = (currentDate - 30*24*60*60*1000);
      newData = allData.filter(sample => sample[0] >= end);
    }
    else if(index == 2){
      var end = (currentDate - 90*24*60*60*1000);
      newData = allData.filter(sample => sample[0] >= end);
    }
    else if(index == 3){
      var end = (currentDate - 365*24*60*60*1000);
      newData = allData.filter(sample => sample[0] >= end);
    }
    else if(index == 4){
      newData = allData;
    }
    setData(newData);
  }


  return (
    <Animated.View style={{elevation: dragging.elevation, zIndex: dragging.zIndex, width:'100%', alignItems: 'center', transform: [{ translateX: 0 }, { translateY: pan.y }]}}>
      <View style={[{shadowRadius: dragging.shadowRadius}, styles.chartContainer]}>
        <View style={{flex: 1, textAlign: 'left'}}>
          <View style={{flex: 1, flexDirection: 'row', justifyContent: 'space-between'}}>
            <Text style={styles.cryptoSymbol}>{props.symbol}</Text>
            <View style={{width: 20}} {...Drag.panHandlers}>
              <Icon name="grip-lines" size={20} color="#c0c0c7" />
            </View>
          </View>
          <Text style={styles.cryptoName}>{props.name}</Text>
        </View>

        <View style={{flex: 1, height: 20, textAlign: 'center', marginBottom: 20}}>
          <PriceViewer touchX={touchX} deviceWidth={width} data={data}></PriceViewer>
        </View>

        <View style={{ height: 80, flexDirection: 'row', marginBottom: 15, marginTop: 15 }}>
          <View style={{ flex: 1}} {...panResponder.panHandlers}>
            <AreaChart
              style={{ flex:1, height: 80}}
              data={data.map(x => x[1])}
              contentInset={{ top: 10, bottom: 10 }}
              curve={shape.curveNatural}
              svg={{ fill: 'url(#gradient)' }}
              >
              <Tooltip touchX={touchX} deviceWidth={width} data={data}/>
              <ChartLine/>
              <Gradient/>
            </AreaChart>
          </View>
        </View>

        <View style={{flex: 1, alignItems: 'center'}}>
          <View style={styles.buttonsContainer}>
            <View style={period[0] == 1 ? [styles.button, {backgroundColor: 'rgb(41, 114, 255)'}]:styles.button} onStartShouldSetResponder={() => buttonCLicked(0)}>
              <Text style={period[0] == 1 ? [styles.buttonText, {color: '#ffffff'}]:styles.buttonText}>
                1W
              </Text>
            </View>
            <View style={period[1] == 1 ? [styles.button, {backgroundColor: 'rgb(41, 114, 255)'}]:styles.button} onStartShouldSetResponder={() => buttonCLicked(1)}>
              <Text style={period[1] == 1 ? [styles.buttonText, {color: '#ffffff'}]:styles.buttonText}>
                1M
              </Text>
            </View>
            <View style={period[2] == 1 ? [styles.button, {backgroundColor: 'rgb(41, 114, 255)'}]:styles.button} onStartShouldSetResponder={() => buttonCLicked(2)}>
              <Text style={period[2] == 1 ? [styles.buttonText, {color: '#ffffff'}]:styles.buttonText}>
                3M
              </Text>
            </View>
            <View style={period[3] == 1 ? [styles.button, {backgroundColor: 'rgb(41, 114, 255)'}]:styles.button} onStartShouldSetResponder={() => buttonCLicked(3)}>
              <Text style={period[3] == 1 ? [styles.buttonText, {color: '#ffffff'}]:styles.buttonText}>
                1Y
              </Text>
            </View>
            <View style={period[4] == 1 ? [styles.button, {backgroundColor: 'rgb(41, 114, 255)'}]:styles.button} onStartShouldSetResponder={() => buttonCLicked(4)}>
              <Text style={period[4] == 1 ? [styles.buttonText, {color: '#ffffff'}]:styles.buttonText}>
                3Y
              </Text>
            </View>
          </View>
        </View>
      </View>
    </Animated.View>
  );
};

const styles = StyleSheet.create({
  container: {
    flexGrow: 0,
    justifyContent: 'flex-start',
    marginTop: 70,
    paddingBottom: 100,
  },
  topBar: {
    position: 'absolute',
    zIndex: 200,
    top: 0,
    height: 60,
    justifyContent: 'flex-end',
    alignItems: 'center',
    width: '100%',
    flex: 1,
    backgroundColor: "#fff",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 1,
    },
    shadowOpacity: 0.1,
    shadowRadius: 5,
    paddingBottom: 8
  },
  chartContainer: {
    backgroundColor: '#ffffff',
    width: '92%',
    borderRadius: 20,
    padding: 20,
    marginBottom: 20,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25
  },
  cryptoSymbol: {
    fontFamily: 'Poppins_500Medium',
    fontSize: 25,
    color: '#000000'
  },
  cryptoName: {
    fontFamily: 'Poppins_500Medium',
    fontSize: 20,
    color: '#000000'
  },
  currentPrice: {
    fontFamily: 'Poppins_500Medium',
    fontSize: 18,
    color: '#000000',
    margin: 0
  },
  currentDate: {
    fontFamily: 'Poppins_500Medium',
    fontSize: 12,
    color: '#000000',
    margin: 0
  },
  text: {
    fontFamily: 'Poppins_600SemiBold',
    fontSize: 30,
    color: '#000000'
  },
  buttonsContainer: {
    width: '80%', 
    flexDirection: 'row', 
    justifyContent: 'space-between'
  },
  button: {
    paddingTop: 5,
    paddingBottom: 5,
    paddingLeft: 10,
    paddingRight: 10,
    borderRadius: 30
  },
  buttonText: {
    fontFamily: 'Poppins_500Medium',
    fontSize: 12,
    color: '#000000'
  },
});
