import 'react-native-gesture-handler';
import { StyleSheet, Text, View, PanResponder } from 'react-native';
import React, {useState,useEffect} from 'react';
import { useFonts, Poppins_300Light, Poppins_400Regular, Poppins_500Medium, Poppins_600SemiBold} from '@expo-google-fonts/poppins';
import { AppLoading } from 'expo';
import * as firebase from 'firebase'
import "firebase/functions";
import { AreaChart} from 'react-native-svg-charts'
import * as shape from 'd3-shape'
import { Defs, LinearGradient, Stop, Path} from 'react-native-svg'
import PriceViewer from './PriceViewer'
import TouchHandler from './TouchHandler'


export default function Predictions() {
  const [loading, setLoading] = useState(true);
  const [cryptoData, setCryptoData] = useState([]);
  let [fontsLoaded] = useFonts({
    Poppins_300Light,
    Poppins_400Regular,
    Poppins_500Medium,
    Poppins_600SemiBold
  });

  var endDate = new Date();
  endDate = endDate.getFullYear() + "-" + (endDate.getMonth() + 1) + "-" + endDate.getDate();

  var startDate = new Date((new Date()).getTime() - 5*365*24*60*60*1000);
  startDate = startDate.getFullYear() + "-" + (startDate.getMonth() + 1) + "-" + startDate.getDate();

  useEffect(()=>{
    if(loading && !fontsLoaded){
    firebase.functions().httpsCallable('getCryptoData')({assets: ['BTC'], start: startDate, end: endDate, interval: '1d'}).then(response =>{
      if(response.data.statusCode == 200){
        setCryptoData(response.data.body);
        setLoading(false);
      }
      else{
        console.error(response.data.body);
      }
    }).catch(error =>{
      console.error(error);
    })
  }},[])

  if (!fontsLoaded) {
    return <AppLoading/>;
  }
  else{
    if(!loading){
    return (
      <View style={styles.container}>
        {cryptoData.map((crypto)=><Chart name={crypto.name} symbol={crypto.symbol} data={(crypto.values).map(x => [x[0], x[4]])}></Chart>)}
      </View>
    );
    }
    else{
      return(
        <View style={styles.container}>
          <Text style={styles.text}>
              LOADING...
          </Text>
        </View>
      );
    }
  }
};


const Chart = (props) => {
  const allData = props.data;
  const [data, setData] = useState(allData.filter(sample => sample[0] >= ((new Date).getTime() - 30*24*60*60*1000)));
  const [swipeIndex, setSwipeIndex] = useState(-1);
  const [period, setPeriod] = useState([0,1,0,0,0]);

/*   const Tooltip = ({ x, y }) => (
    swipeIndex >= 0 && swipeIndex <= data.length ? (
      <G x={ x(swipeIndex) - (75 / 2) }>
      <G x={ 75 / 2 }>
          <Line
            y1={ 120 }
            y2={ 0 }
            stroke={ 'rgba(41, 114, 255, 0.7)' }
            strokeWidth={ 1 }
          />
          <Circle
              cy={ y(data[swipeIndex][1])}
              r={ 4 }
              fill={ 'rgb(41, 114, 255)' }
          />
      </G>
    </G>
    ):null
  ) */

/*   const PriceViewer = () => {
    return(
      swipeIndex >= 0 && swipeIndex <= data.length ? (
        <View>
          <Text style={styles.currentPrice}>{parsePrice(data[swipeIndex][1])}</Text>
          <Text style={styles.currentDate}>{getDate(data[swipeIndex][0])}
          </Text>
        </View>
      ):null
    );
  } */

/*   const panResponder = React.useRef(
    PanResponder.create({
      onStartShouldSetPanResponder: () => true,
      onPanResponderGrant: (evt) => {
        if(evt.nativeEvent.locationX >= 0 && evt.nativeEvent.locationX <= evt.currentTarget.clientWidth){
          var index = Math.floor((evt.nativeEvent.locationX / evt.currentTarget.clientWidth) * data.length);
          setSwipeIndex(index);
        }
      },
      onPanResponderMove: (evt) => {
        if(evt.nativeEvent.locationX >= 0 && evt.nativeEvent.locationX <= evt.currentTarget.clientWidth){
          var index = Math.floor((evt.nativeEvent.locationX / evt.currentTarget.clientWidth) * data.length);
          setSwipeIndex(index);
        }
      },
      onPanResponderRelease: () => {
        setSwipeIndex(-1);
      }
    })
  ).current; */


  function buttonCLicked(index){
    var newPeriod = [0,0,0,0,0];
    newPeriod[index] = 1;
    setPeriod(newPeriod);
    var currentDate = (new Date()).getTime();
    var newData;

    if(index == 0){
      var end = (currentDate - 7*24*60*60*1000);
      newData = allData.filter(sample => sample[0] >= end);
    }
    else if(index == 1){
      var end = (currentDate - 30*24*60*60*1000);
      newData = allData.filter(sample => sample[0] >= end);
    }
    else if(index == 2){
      var end = (currentDate - 90*24*60*60*1000);
      newData = allData.filter(sample => sample[0] >= end);
    }
    else if(index == 3){
      var end = (currentDate - 365*24*60*60*1000);
      newData = allData.filter(sample => sample[0] >= end);
    }
    else if(index == 4){
      newData = allData;
    }
    setData(newData);
  }

  const foo = (index) => {
    setSwipeIndex(index);
  }

  return (
    <View style={styles.chartContainer}>
      <View style={{flex: 1, textAlign: 'left'}}>
        <Text style={styles.cryptoSymbol}>{props.symbol}</Text>
        <Text style={styles.cryptoName}>{props.name}</Text>
      </View>

      <View style={{flex: 1, height: 20, textAlign: 'center', marginTop: 10, marginBottom: 20}}>
        <PriceViewer swipeIndex={swipeIndex} data={data}></PriceViewer>
      </View>

      <View style={{ height: 120, flexDirection: 'row', marginBottom: 15, marginTop: 15 }}>
        <TouchHandler data={data} setIndex={foo}>
        </TouchHandler>
      </View>

      <View style={{flex: 1, alignItems: 'center', marginTop: 20}}>
        <View style={styles.buttonsContainer}>
          <View style={period[0] == 1 ? [styles.button, {backgroundColor: 'rgb(41, 114, 255)'}]:styles.button} onStartShouldSetResponder={() => buttonCLicked(0)}>
            <Text style={period[0] == 1 ? [styles.buttonText, {color: '#ffffff'}]:styles.buttonText}>
              1W
            </Text>
          </View>
          <View style={period[1] == 1 ? [styles.button, {backgroundColor: 'rgb(41, 114, 255)'}]:styles.button} onStartShouldSetResponder={() => buttonCLicked(1)}>
            <Text style={period[1] == 1 ? [styles.buttonText, {color: '#ffffff'}]:styles.buttonText}>
              1M
            </Text>
          </View>
          <View style={period[2] == 1 ? [styles.button, {backgroundColor: 'rgb(41, 114, 255)'}]:styles.button} onStartShouldSetResponder={() => buttonCLicked(2)}>
            <Text style={period[2] == 1 ? [styles.buttonText, {color: '#ffffff'}]:styles.buttonText}>
              3M
            </Text>
          </View>
          <View style={period[3] == 1 ? [styles.button, {backgroundColor: 'rgb(41, 114, 255)'}]:styles.button} onStartShouldSetResponder={() => buttonCLicked(3)}>
            <Text style={period[3] == 1 ? [styles.buttonText, {color: '#ffffff'}]:styles.buttonText}>
              1Y
            </Text>
          </View>
          <View style={period[4] == 1 ? [styles.button, {backgroundColor: 'rgb(41, 114, 255)'}]:styles.button} onStartShouldSetResponder={() => buttonCLicked(4)}>
            <Text style={period[4] == 1 ? [styles.buttonText, {color: '#ffffff'}]:styles.buttonText}>
              5Y
            </Text>
          </View>
        </View>
      </View>
    </View>
  );
};


const styles = StyleSheet.create({
  container: {
    flex:1,
    backgroundColor: '#e9f0ff',
    alignItems: 'center',
    justifyContent: 'flex-start',
    paddingTop: 30
  },
  chartContainer: {
    backgroundColor: '#ffffff',
    width: '90%',
    borderRadius: 20,
    padding: 20,
    marginBottom: 20
  },
  cryptoSymbol: {
    fontFamily: 'Poppins_500Medium',
    fontSize: 25,
    color: '#000000'
  },
  cryptoName: {
    fontFamily: 'Poppins_500Medium',
    fontSize: 20,
    color: '#000000'
  },
  currentPrice: {
    fontFamily: 'Poppins_500Medium',
    fontSize: 18,
    color: '#000000',
    margin: 0
  },
  currentDate: {
    fontFamily: 'Poppins_500Medium',
    fontSize: 12,
    color: '#000000',
    margin: 0
  },
  text: {
    fontFamily: 'Poppins_600SemiBold',
    fontSize: 30,
    color: '#000000'
  },
  buttonsContainer: {
    width: '80%', 
    flexDirection: 'row', 
    justifyContent: 'space-between'
  },
  button: {
    paddingTop: 5,
    paddingBottom: 5,
    paddingLeft: 10,
    paddingRight: 10,
    borderRadius: 30
  },
  buttonText: {
    fontFamily: 'Poppins_500Medium',
    fontSize: 12,
    color: '#000000'
  }
});
