export default function Predictions() {
  const [loading, setLoading] = useState(true);
  const [cryptoData, setCryptoData] = useState({});
  let [fontsLoaded] = useFonts({
    Poppins_300Light,
    Poppins_400Regular,
    Poppins_500Medium,
    Poppins_600SemiBold
  });

  useEffect(()=>{
    if(loading && !fontsLoaded){
    firebase.functions().httpsCallable('getCryptoData')({asset: 'bitcoin', start: '2020-01-01', end: '2020-02-01', interval: '1d'}).then(response =>{
      if(response.data.statusCode == 200){
        setCryptoData(response.data.body);
        setLoading(false);
      }
      else{
        console.error(response.data.body);
      }
    }).catch(error =>{
      console.error(error);
    })
  }},[])

  if (!fontsLoaded) {
    return <AppLoading/>;
  }
  else{
    if(!loading){
      let data = [];
      //console.log(cryptoData.values);
      (cryptoData.values)
      for(var sample in cryptoData.values){
        console.log(sample);
      }
      return (
        <View style={styles.container}>
          <View style={styles.chartContainer}>
            <Text style={styles.cryptoSymbol}>{cryptoData.symbol}</Text>
            <Text style={styles.cryptoName}>{cryptoData.name}</Text>
            <AreaChart
              style={{ height: 200 }}
              data={data}
              contentInset={{ top: 30, bottom: 30 }}
              curve={shape.curveNatural}
              svg={{ fill: 'rgba(41, 114, 255, 0.7)' }}>
              <Grid/>
            </AreaChart>
            <View style={styles.buttonsContainer}>
              <View style={styles.button}>
                <Text style={styles.buttonText}>
                  Week
                </Text>
              </View>
              <View style={styles.button}>
                <Text style={styles.buttonText}>
                  Month
                </Text>
              </View>
              <View style={styles.button}>
                <Text style={styles.buttonText}>
                  Year
                </Text>
              </View>
            </View>
          </View>
        </View>
      );
    }
    else{
      return(
        <View style={styles.container}>
          <Text style={styles.text}>
              LOADING...
          </Text>
        </View>
      );
    }
  }
};

const styles = StyleSheet.create({
  container: {
    flex:1,
    backgroundColor: '#e9f0ff',
    alignItems: 'center',
    justifyContent: 'from-start',
    paddingTop: 30
  },
  chartContainer: {
    backgroundColor: '#ffffff',
    width: '90%',
    borderRadius: 20,
    padding: 20,
    textAlign: 'left'
  },
  cryptoSymbol: {
    fontFamily: 'Poppins_500Medium',
    fontSize: 25,
    color: '#000000'
  },
  cryptoName: {
    fontFamily: 'Poppins_500Medium',
    fontSize: 20,
    color: '#000000'
  },
  text: {
    fontFamily: 'Poppins_600SemiBold',
    fontSize: 30,
    color: '#000000'
  },
  buttonsContainer: {
    flex: 1,
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    marginTop: 20
  },
  button: {
    backgroundColor: '#2970ff',
    borderRadius: 20,
    padding: 10,
    paddingLeft: 20,
    paddingRight: 20
  },
  buttonText: {
    fontFamily: 'Poppins_400Regular',
    fontSize: 15,
    color: '#ffffff'
  }
});
