import { StatusBar } from 'expo-status-bar';
import 'react-native-gesture-handler';
import { StyleSheet, Text, View } from 'react-native';
import React, {useState,useEffect} from 'react';
import { useFonts, Poppins_300Light, Poppins_400Regular, Poppins_500Medium, Poppins_600SemiBold} from '@expo-google-fonts/poppins';
import { AppLoading } from 'expo';
import * as firebase from 'firebase'
import "firebase/functions";


export default function Predictions() {
  const [loading, setLoading] = useState(true);
  const [cryptoList, setCryptoData] = useState([]);
  let [fontsLoaded] = useFonts({
    Poppins_300Light,
    Poppins_400Regular,
    Poppins_500Medium,
    Poppins_600SemiBold
  });


  useEffect(()=>{
    if(loading && !fontsLoaded){
    firebase.functions().httpsCallable('getCryptoData')({asset: 'bitcoin', start: '2020-01-01', end: '2020-02-01', interval: '1d'}).then(
        (response)=> {
            console.log(response);
            setCryptoData(response);
            setLoading(false);
        }
    ).catch(
        (error)=> {
            console.log(error);
        }
    )
  }})

  if (!fontsLoaded) {
    return <AppLoading/>;
  }
  else{
    if(!loading){
      return (
        <View style={styles.container}>
          <View style={styles.chartContainer}>
            <Text style={styles.text}>{t}</Text>
          </View>
        </View>
      );
    }
    else{
      return(
        <View style={styles.container}>
          <Text style={styles.text}>
              LOADING...
          </Text>
        </View>
      );
    }
  }
};

const styles = StyleSheet.create({
  container: {
    flex:1,
    backgroundColor: '#f3ffff',
    alignItems: 'center',
    justifyContent: 'from-start',
    paddingTop: 30
  },
  chartContainer: {
    backgroundColor: '#ffffff',
    width: '90%',
    height: '30%',
    borderRadius: 10,
    padding: 10,
    textAlign: 'left',
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 1,
    },
    shadowOpacity: 0.2,
    shadowRadius: 3.84,
    elevation: 1,
  },
  chartText: {
    fontFamily: 'Poppins_500Medium',
    fontSize: 20,
    color: '#000000'
  },
  text: {
    fontFamily: 'Poppins_600SemiBold',
    fontSize: 30,
    color: '#000000'
  }
});
