import 'react-native-gesture-handler';
import { Animated, StyleSheet, Text, TouchableOpacity, ScrollView, FlatList, View, PanResponder, Dimensions, SafeAreaView} from 'react-native';
import React, {useState,useEffect,useRef} from 'react';
import { useFonts, Poppins_300Light, Poppins_400Regular, Poppins_500Medium, Poppins_600SemiBold} from '@expo-google-fonts/poppins';
import { AppLoading } from 'expo';
import * as firebase from 'firebase'
import "firebase/functions";
import { AreaChart} from 'react-native-svg-charts'
import * as shape from 'd3-shape'
import { Defs, LinearGradient, Stop, Path} from 'react-native-svg'
import Tooltip from './Tooltip'
import PriceViewer from './PriceViewer'
import Icon from 'react-native-vector-icons/FontAwesome5'
import DraggableFlatList from "react-native-draggable-flatlist";

/* 
export default function Predictions() {
  const [loading, setLoading] = useState(true);
  const [cryptoData, setCryptoData] = useState([]);
  const [scrollable, setScrollable] = useState(true);
  const flatListRef = React.useRef()
  let [fontsLoaded] = useFonts({
    Poppins_300Light,
    Poppins_400Regular,
    Poppins_500Medium,
    Poppins_600SemiBold
  });

  const setScroll = (scroll) => {
    setScrollable(scroll);
  }

  const moveItem = (old_index, new_index) => {
    var arr = cryptoData.slice();
    if (new_index >= arr.length) {
        var k = new_index - arr.length + 1;
        while (k--) {
            arr.push(undefined);
        }
    }
    arr.splice(new_index, 0, arr.splice(old_index, 1)[0]);
    setCryptoData(arr);
  };

  const scrollTo = (offset) => {
    flatListRef.current.scrollToOffset({ animated: true, offset: offset })
  }

  const renderItem = ({ item, index }) => {
    return (
      <Chart name={item.name} symbol={item.symbol} index={index} scrollTo={scrollTo} dataLength={cryptoData.length} scroll={setScroll} moveItem={moveItem} data={(item.values).map(x => [x[0], x[4]])}/>
    );
  };

  var endDate = new Date();
  endDate = endDate.getFullYear() + "-" + (endDate.getMonth() + 1) + "-" + endDate.getDate();

  var startDate = new Date((new Date()).getTime() - 3*365*24*60*60*1000);
  startDate = startDate.getFullYear() + "-" + (startDate.getMonth() + 1) + "-" + startDate.getDate();

  useEffect(()=>{
    if(loading && !fontsLoaded){
    firebase.functions().httpsCallable('getCryptoData')({assets: ['BTC', 'ETH', 'LTC', 'ADA', 'DOT', 'XLM'], start: startDate, end: endDate, interval: '1d'}).then(response =>{
      if(response.data.statusCode == 200){
        setCryptoData(response.data.body);
        setLoading(false);
      }
      else{
        console.error(response.data.body);
      }
    }).catch(error =>{
      console.error(error);
    })
  }},[])

  if (!fontsLoaded) {
    return <AppLoading/>;
  }
  else{
    if(!loading){
    return (
      <SafeAreaView style={{flex: 1, backgroundColor:'#e9f0ff'}}>
        <View style={styles.topBar}>
          <Text style={[styles.text, {fontSize: 18}]}>CryptoSimulator</Text>
        </View>
        <FlatList data={cryptoData} renderItem={renderItem} ref={flatListRef} contentContainerStyle={styles.container} showsVerticalScrollIndicator={false} scrollEnabled={scrollable}/>
      </SafeAreaView>
    );
    }
