export default function Predictions() {
  const [loading, setLoading] = useState(true);
  const [cryptoData, setCryptoData] = useState([]);
  const [scrollable, setScrollable] = useState(true);
  const flatListRef = React.useRef();
  let [fontsLoaded] = useFonts({
    Poppins_300Light,
    Poppins_400Regular,
    Poppins_500Medium,
    Poppins_600SemiBold
  });


  const setScroll = (scroll) => {
    setScrollable(scroll);
  }

  const renderItem = ({ item, index, drag, isActive }) =>{
    return (
      <View style={[styles.chartContainer, {shadowRadius: isActive ? 20 : 5}]}>
        <TouchableOpacity onLongPress={drag} style={{position: 'absolute', top: 20, right: 20}}>
          <Icon name="grip-lines" size={20} color="#c0c0c7"/>
        </TouchableOpacity>
        <Chart></Chart>
      </View>
    );
  };

  var endDate = new Date();
  endDate = endDate.getFullYear() + "-" + (endDate.getMonth() + 1) + "-" + endDate.getDate();

  var startDate = new Date((new Date()).getTime() - 3*365*24*60*60*1000);
  startDate = startDate.getFullYear() + "-" + (startDate.getMonth() + 1) + "-" + startDate.getDate();

  useEffect(()=>{
    if(loading && !fontsLoaded){
    firebase.functions().httpsCallable('getCryptoData')({assets: ['BTC', 'ETH', 'LTC', 'ADA', 'DOT', 'XLM'], start: startDate, end: endDate, interval: '1d'}).then(response =>{
      if(response.data.statusCode == 200){
        setCryptoData(response.data.body);
        setLoading(false);
      }
      else{
        console.error(response.data.body);
      }
    }).catch(error =>{
      console.error(error);
    })
  }},[])

  if (!fontsLoaded) {
    return <AppLoading/>;
  }
  else{
    if(!loading){
      return (
        <SafeAreaView style={{flex: 1, backgroundColor:'#e9f0ff'}}>
          <View style={styles.topBar}>
            <Text style={[styles.text, {fontSize: 18}]}>CryptoSimulator</Text>
          </View>
          <DraggableFlatList data={cryptoData} contentContainerStyle={styles.container} ref={flatListRef} renderItem={renderItem} showsVerticalScrollIndicator={false} scrollEnabled={scrollable} keyExtractor={(item, index) => `draggable-item-${item.key}`} onDragEnd={({ data }) => setCryptoData(data)}/>
        </SafeAreaView>
      );
    }
    else{
      return(
        <View style={{flex: 1, alignItems: 'center', justifyContent: 'center'}}>
          <View style={styles.topBar}>
            <Text style={[styles.text, {fontSize: 18}]}>CryptoSimulator</Text>
          </View>
          <Text style={styles.text}>
              LOADING...
          </Text>
        </View>
      );
    }
  }
}


