import { StatusBar } from 'expo-status-bar';
import 'react-native-gesture-handler';
import { Button, StyleSheet, Text, View } from 'react-native';
import React, {useState,useEffect} from 'react';
import { useFonts, Poppins_300Light, Poppins_400Regular, Poppins_500Medium, Poppins_600SemiBold} from '@expo-google-fonts/poppins';
import { AppLoading } from 'expo';
import * as firebase from 'firebase'
import "firebase/functions";
import { AreaChart, Grid, XAxis, YAxis} from 'react-native-svg-charts'
import * as shape from 'd3-shape'
import { Defs, LinearGradient, Stop, Path, G } from 'react-native-svg'

export default function Predictions() {
  const [loading, setLoading] = useState(true);
  const [cryptoData, setCryptoData] = useState({});
  let [fontsLoaded] = useFonts({
    Poppins_300Light,
    Poppins_400Regular,
    Poppins_500Medium,
    Poppins_600SemiBold
  });

  const axesSvg = { fontSize: 10, fill: 'grey', fontFamily:  'Poppins_400Regular'};

  const Line = ({ line }) => (
    <Path
        key={'line'}
        d={line}
        stroke={'rgb(41, 114, 255)'}
        fill={'none'}
    />
  )

  const Gradient = ({ index }) => (
    <Defs key={index}>
        <LinearGradient id={'gradient'} x1={'0%'} y1={'0%'} x2={'0%'} y2={'100%'}>
            <Stop offset={'0%'} stopColor={'rgb(41, 114, 255)'} stopOpacity={0.2}/>
            <Stop offset={'28%'} stopColor={'rgb(41, 114, 255)'} stopOpacity={0}/>
        </LinearGradient>
    </Defs>
  )

  const data = [ 50, 10, 40, 95, -4, -24, 85, 91, 35, 53, -53, 24, 50, -20, -80 ]

  const CustomGrid = ({ x, y, data, ticks }) => (
    <G>
        {
            ticks.map(tick => (
                <Line
                    key={ tick }
                    x1={ '0%' }
                    x2={ '100%' }
                    y1={ y(tick) }
                    y2={ y(tick) }
                    stroke={ 'rgba(0,0,0,0.2)' }
                />
            ))
        }
        {
            data.map((_, index) => (
                <Line
                    key={ index }
                    y1={ '0%' }
                    y2={ '100%' }
                    x1={ x(index) }
                    x2={ x(index) }
                    stroke={ 'rgba(0,0,0,0.2)' }
                />
            ))
        }
    </G>
)

  useEffect(()=>{
    if(loading && !fontsLoaded){
    firebase.functions().httpsCallable('getCryptoData')({asset: 'bitcoin', start: '2020-01-01', end: '2020-02-01', interval: '1d'}).then(response =>{
      if(response.data.statusCode == 200){
        setCryptoData(response.data.body);
        setLoading(false);
      }
      else{
        console.error(response.data.body);
      }
    }).catch(error =>{
      console.error(error);
    })
  }},[])

  if (!fontsLoaded) {
    return <AppLoading/>;
  }
  else{
    if(!loading){
      let data = [];
      (cryptoData.values).forEach(sample =>{
        data.push(sample[4]);
      })
      return (
        <View style={styles.container}>
          <View style={styles.chartContainer}>
            <Text style={styles.cryptoSymbol}>{cryptoData.symbol}</Text>
            <Text style={styles.cryptoName}>{cryptoData.name}</Text>

            <View style={{ height: 200, flexDirection: 'row', marginBottom: 15, marginTop: 15 }}>
                <YAxis
                    data={data}
                    contentInset={{ top: 10, bottom: 10 }}
                    svg={axesSvg}
                    numberOfTicks={ 6 }
                />
                <View style={{ flex: 1, marginLeft: 10 }}>
                  <AreaChart
                    style={{ height: 200 }}
                    data={data}
                    contentInset={{ top: 10, bottom: 10 }}
                    curve={shape.curveNatural}
                    svg={{ fill: 'url(#gradient)' }}
                    >
                    <CustomGrid/>
                    <Line/>
                    <Gradient/>
                  </AreaChart>
                    <XAxis
                        style={{ marginHorizontal: -10, marginTop: 5}}
                        data={data}
                        formatLabel={(value, index) => index + 1 }
                        contentInset={{ left: 10, right: 10 }}
                        svg={axesSvg}
                        numberOfTicks={ 15 }
                    />
                </View>
            </View>

            <View style={styles.buttonsContainer}>
              <View style={styles.button}>
                <Text style={styles.buttonText}>
                  Week
                </Text>
              </View>
              <View style={styles.button}>
                <Text style={styles.buttonText}>
                  Month
                </Text>
              </View>
              <View style={styles.button}>
                <Text style={styles.buttonText}>
                  Year
                </Text>
              </View>
            </View>
          </View>
        </View>
      );
    }
    else{
      return(
        <View style={styles.container}>
          <Text style={styles.text}>
              LOADING...
          </Text>
        </View>
      );
    }
  }
};

const styles = StyleSheet.create({
  container: {
    flex:1,
    backgroundColor: '#e9f0ff',
    alignItems: 'center',
    justifyContent: 'flex-start',
    paddingTop: 30
  },
  chartContainer: {
    backgroundColor: '#ffffff',
    width: '90%',
    borderRadius: 20,
    padding: 20,
    textAlign: 'left'
  },
  cryptoSymbol: {
    fontFamily: 'Poppins_500Medium',
    fontSize: 25,
    color: '#000000'
  },
  cryptoName: {
    fontFamily: 'Poppins_500Medium',
    fontSize: 20,
    color: '#000000'
  },
  text: {
    fontFamily: 'Poppins_600SemiBold',
    fontSize: 30,
    color: '#000000'
  },
  buttonsContainer: {
    flex: 1,
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    marginTop: 20
  },
  button: {
    backgroundColor: '#2970ff',
    borderRadius: 20,
    padding: 10,
    paddingLeft: 20,
    paddingRight: 20
  },
  buttonText: {
    fontFamily: 'Poppins_400Regular',
    fontSize: 15,
    color: '#ffffff'
  }
});
