export default function Predictions() {
  const [loading, setLoading] = useState(true);
  const [cryptoData, setCryptoData] = useState({});
  let [fontsLoaded] = useFonts({
    Poppins_300Light,
    Poppins_400Regular,
    Poppins_500Medium,
    Poppins_600SemiBold
  });


  useEffect(()=>{
    if(loading && !fontsLoaded){
    firebase.functions().httpsCallable('getCryptoData')({asset: 'bitcoin', start: '2020-01-01', end: '2020-02-01', interval: '1d'}).then(response =>{
      if(response.data.statusCode == 200){
        setCryptoData(response.data.body);
        setLoading(false);
      }
      else{
        console.error(response.data.body);
      }
    }).catch(error =>{
      console.error(error);
    })
  }})

  if (!fontsLoaded) {
    return <AppLoading/>;
  }
  else{
    if(!loading){
      console.log("CRYPTODATA: ", cryptoData);
      return (
        <View style={styles.container}>
          <View style={styles.chartContainer}>
            <Text style={styles.cryptoSymbol}>{cryptoData.symbol}</Text>
            <Text style={styles.cryptoName}>{cryptoData.name}</Text>
            <View style={styles.buttonsContainer}>
              <Button style={styles.button} title='Week'/>
              <Button style={styles.button} title='Month'/>
              <Button style={styles.button} title='Year'/>
            </View>
          </View>
        </View>
      );
    }
    else{
      return(
        <View style={styles.container}>
          <Text style={styles.text}>
              LOADING...
          </Text>
        </View>
      );
    }
  }
};

const styles = StyleSheet.create({
  container: {
    flex:1,
    backgroundColor: '#f3ffff',
    alignItems: 'center',
    justifyContent: 'from-start',
    paddingTop: 30
  },
  chartContainer: {
    backgroundColor: '#ffffff',
    width: '90%',
    borderRadius: 10,
    padding: 20,
    textAlign: 'left',
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 1,
    },
    shadowOpacity: 0.2,
    shadowRadius: 3.84,
    elevation: 1,
  },
  cryptoSymbol: {
    fontFamily: 'Poppins_500Medium',
    fontSize: 25,
    color: '#000000'
  },
  cryptoName: {
    fontFamily: 'Poppins_500Medium',
    fontSize: 20,
    color: '#000000'
  },
  text: {
    fontFamily: 'Poppins_600SemiBold',
    fontSize: 30,
    color: '#000000'
  },
  buttonsContainer: {
    flex: 1,
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    marginTop: 20
  },
  button: {

  }
});
