import 'react-native-gesture-handler';
import { StyleSheet, Text, View, ScrollView, PanResponder } from 'react-native';
import React, {useState,useEffect} from 'react';
import { useFonts, Poppins_300Light, Poppins_400Regular, Poppins_500Medium, Poppins_600SemiBold} from '@expo-google-fonts/poppins';
import { AppLoading } from 'expo';
import * as firebase from 'firebase'
import "firebase/functions";
import { AreaChart, Grid, LineChart} from 'react-native-svg-charts'
import * as shape from 'd3-shape'
import { Defs, LinearGradient, Stop, Path, G, Line, Circle} from 'react-native-svg'
import { event } from 'react-native-reanimated';


export default function Predictions() {
  const [loading, setLoading] = useState(true);
  const [cryptoData, setCryptoData] = useState({});
  let [fontsLoaded] = useFonts({
    Poppins_300Light,
    Poppins_400Regular,
    Poppins_500Medium,
    Poppins_600SemiBold
  });

  useEffect(()=>{
    if(loading && !fontsLoaded){
    firebase.functions().httpsCallable('getCryptoData')({asset: 'bitcoin', start: '2020-01-01', end: '2020-02-01', interval: '1d'}).then(response =>{
      if(response.data.statusCode == 200){
        setCryptoData(response.data.body);
        setLoading(false);
      }
      else{
        console.error(response.data.body);
      }
    }).catch(error =>{
      console.error(error);
    })
  }},[])

  if (!fontsLoaded) {
    return <AppLoading/>;
  }
  else{
    if(!loading){
      let data = [];
      (cryptoData.values).forEach(sample =>{
        console.log(sample[0])
        data.push([sample[0], sample[4]]);
      })

    return (
      <View style={styles.container}>
        <Chart name={cryptoData.name} symbol={cryptoData.symbol} data={data}></Chart>
      </View>
    );
    }
    else{
      return(
        <View style={styles.container}>
          <Text style={styles.text}>
              LOADING...
          </Text>
        </View>
      );
    }
  }
};


const Chart = (props) => {
  const [data, setData] = useState(props.data);
  const [swipeIndex, setSwipeIndex] = useState(-1);
  const [period, setPeriod] = useState([1,0,0,0,0]);
  var chartWidth;

  const ChartLine = ({ line }) => (
    <Path
        key={'line'}
        d={line}
        stroke={'rgb(41, 114, 255)'}
        fill={'none'}
    />
  )

  const Gradient = ({ index }) => (
    <Defs key={index}>
        <LinearGradient id={'gradient'} x1={'0%'} y1={'0%'} x2={'0%'} y2={'100%'}>
            <Stop offset={'0%'} stopColor={'rgb(41, 114, 255)'} stopOpacity={0.2}/>
            <Stop offset={'28%'} stopColor={'rgb(41, 114, 255)'} stopOpacity={0}/>
        </LinearGradient>
    </Defs>
  )

  const Tooltip = ({ x, y }) => (
    swipeIndex != -1 ? (
      <G x={ x(swipeIndex) - (75 / 2) }>
      <G x={ 75 / 2 }>
          <Line
            y1={ 120 }
            y2={ 0 }
            stroke={ 'rgba(41, 114, 255, 0.7)' }
            strokeWidth={ 1 }
          />
          <Circle
              cy={ y(data[swipeIndex][1])}
              r={ 4 }
              fill={ 'rgb(41, 114, 255)' }
          />
      </G>
    </G>
    ):null
  )

  const PriceViewer = () => {
    return(
      swipeIndex != -1 ? (
        <View>
          <Text style={styles.currentPrice}>{data[swipeIndex][1].toFixed(2) + "$"}</Text>
          <Text>{getDate(data[swipeIndex][0])}
          </Text>
        </View>
      ):null
    );
  }

  const panResponder = React.useRef(
    PanResponder.create({
      onStartShouldSetPanResponder: () => true,
      onPanResponderGrant: (evt) => {
        if(evt.nativeEvent.locationX >= 0 && evt.nativeEvent.locationX <= chartWidth){
          var index = Math.floor((evt.nativeEvent.locationX / chartWidth) * data.length);
          setSwipeIndex(index);
        }
      },
      onPanResponderMove: (evt) => {
        if(evt.nativeEvent.locationX >= 0 && evt.nativeEvent.locationX <= chartWidth){
          var index = Math.floor((evt.nativeEvent.locationX / chartWidth) * data.length);
          setSwipeIndex(index);
        }
      },
      onPanResponderRelease: () => {
        setSwipeIndex(-1);
      }
    })
  ).current;

  function buttonCLicked(index){
    var newPeriod = [0,0,0,0,0];
    newPeriod[index] = 1;
    setPeriod(newPeriod);
  }

  function getDate(timestamp){
    var date = new Date(timestamp * 1000);
    var textDate = date.getDay() + " " + date.getDate() + " " + (date.getMonth() + 1) + " " + date.getFullYear();
    console.log(textDate);
    return textDate;
  }

  return (
    <View style={styles.chartContainer}>
      <View style={{flex: 1, textAlign: 'left'}}>
        <Text style={styles.cryptoSymbol}>{props.symbol}</Text>
        <Text style={styles.cryptoName}>{props.name}</Text>
      </View>

      <View style={{flex: 1, height: 20, textAlign: 'center'}}>
        <PriceViewer></PriceViewer>
      </View>

      <View style={{ height: 120, flexDirection: 'row', marginBottom: 15, marginTop: 15 }}>
          <View style={{ flex: 1}} {...panResponder.panHandlers} onLayout={(event) => {chartWidth = event.nativeEvent.layout.width;}}>
            <AreaChart
              style={{ flex:1, height: 120}}
              data={data.map(x => x[1])}
              contentInset={{ top: 10, bottom: 10 }}
              curve={shape.curveNatural}
              svg={{ fill: 'url(#gradient)' }}
              >
              <Tooltip/>
              <ChartLine/>
              <Gradient/>
            </AreaChart>
          </View>
      </View>

      <View style={{flex: 1, alignItems: 'center', marginTop: 20}}>
        <View style={styles.buttonsContainer}>
          <View style={period[0] == 1 ? [styles.button, {backgroundColor: 'rgb(41, 114, 255)'}]:styles.button} onStartShouldSetResponder={() => buttonCLicked(0)}>
            <Text style={period[0] == 1 ? [styles.buttonText, {color: '#ffffff'}]:styles.buttonText}>
              1D
            </Text>
          </View>
          <View style={period[1] == 1 ? [styles.button, {backgroundColor: 'rgb(41, 114, 255)'}]:styles.button} onStartShouldSetResponder={() => buttonCLicked(1)}>
            <Text style={period[1] == 1 ? [styles.buttonText, {color: '#ffffff'}]:styles.buttonText}>
              1W
            </Text>
          </View>
          <View style={period[2] == 1 ? [styles.button, {backgroundColor: 'rgb(41, 114, 255)'}]:styles.button} onStartShouldSetResponder={() => buttonCLicked(2)}>
            <Text style={period[2] == 1 ? [styles.buttonText, {color: '#ffffff'}]:styles.buttonText}>
              1M
            </Text>
          </View>
