import * as firebase from 'firebase'

const firebaseConfig = {
    apiKey: "AIzaSyB2OVyz8NfVJNn013TSEOr_V3YsIBeNRzY",
    authDomain: "cryptosimulator-2020.firebaseapp.com",
    projectId: "cryptosimulator-2020",
    storageBucket: "cryptosimulator-2020.appspot.com",
    messagingSenderId: "923198596355",
    appId: "1:923198596355:web:0795e3fd85aeb627d1b536",
    measurementId: "G-77RHN7PBGX"
};

export default class Firebase {
    static auth;
    static firestore;
    static storage;
    static functions;
    static EmailAuthProvider;

    static init() {
      firebase.initializeApp(firebaseConfig);

      Firebase.auth = firebase.auth();
      Firebase.auth.setPersistence(firebase.auth.Auth.Persistence.LOCAL);
      Firebase.database = firebase.firestore();

      Firebase.storage = firebase.storage();

      Firebase.functions = firebase.functions();
    }
}
