    },
    shadowOpacity: 0.1,
    shadowRadius: 5,
    paddingBottom: 8
  },
  chartContainer: {
    backgroundColor: '#ffffff',
    width: '92%',
    borderRadius: 20,
    padding: 20,
    marginBottom: 20,
