import 'react-native-gesture-handler';
import { StyleSheet, Text, View, ScrollView, PanResponder } from 'react-native';
import React, {useState,useEffect} from 'react';
import { useFonts, Poppins_300Light, Poppins_400Regular, Poppins_500Medium, Poppins_600SemiBold} from '@expo-google-fonts/poppins';
import { AppLoading } from 'expo';
import * as firebase from 'firebase'
import "firebase/functions";
import { AreaChart, Grid, LineChart} from 'react-native-svg-charts'
import * as shape from 'd3-shape'
import { Defs, LinearGradient, Stop, Path, G, Line, Circle} from 'react-native-svg'
import { event } from 'react-native-reanimated';


export default function Predictions() {
  const [loading, setLoading] = useState(true);
  const [cryptoData, setCryptoData] = useState({});
  let [fontsLoaded] = useFonts({
    Poppins_300Light,
    Poppins_400Regular,
    Poppins_500Medium,
    Poppins_600SemiBold
  });

  useEffect(()=>{
    if(loading && !fontsLoaded){
    firebase.functions().httpsCallable('getCryptoData')({asset: 'bitcoin', start: '2020-01-01', end: '2020-02-01', interval: '1d'}).then(response =>{
      if(response.data.statusCode == 200){
        setCryptoData(response.data.body);
        setLoading(false);
      }
      else{
        console.error(response.data.body);
      }
    }).catch(error =>{
      console.error(error);
    })
  }},[])

  if (!fontsLoaded) {
    return <AppLoading/>;
  }
  else{
    if(!loading){
      let data = [];
      console.log(cryptoData);
      (cryptoData.values).forEach(sample =>{
        data.push(sample[4]);
      })

    return (
      <View style={styles.container}>
        <Chart name={cryptoData.name} symbol={cryptoData.symbol} currentPrice={"17.000,00â‚¬"} data={data}></Chart>
      </View>
    );
    }
    else{
      return(
        <View style={styles.container}>
          <Text style={styles.text}>
              LOADING...
          </Text>
        </View>
      );
    }
  }
};


const Chart = (props) => {
  const data = props.data;
  var swipeIndex = 10;
  var chartWidth;

  const ChartLine = ({ line }) => (
    <Path
        key={'line'}
        d={line}
        stroke={'rgb(41, 114, 255)'}
        fill={'none'}
    />
  )

  const Gradient = ({ index }) => (
    <Defs key={index}>
        <LinearGradient id={'gradient'} x1={'0%'} y1={'0%'} x2={'0%'} y2={'100%'}>
            <Stop offset={'0%'} stopColor={'rgb(41, 114, 255)'} stopOpacity={0.2}/>
            <Stop offset={'28%'} stopColor={'rgb(41, 114, 255)'} stopOpacity={0}/>
        </LinearGradient>
    </Defs>
  )

  const Tooltip = ({ x, y }) => (
    <G x={ x(swipeIndex) - (75 / 2) }>
      <G x={ 75 / 2 }>
          <Line
            y1={ 120 }
            y2={ 0 }
            stroke={ 'rgba(41, 114, 255, 0.7)' }
            strokeWidth={ 1 }
          />
          <Circle
              cy={ y(data[swipeIndex]) }
              r={ 4 }
              fill={ 'rgb(41, 114, 255)' }
          />
      </G>
    </G>
  )

  const panResponder = React.useRef(
    PanResponder.create({
      onStartShouldSetPanResponder: () => true,
      onStartShouldSetPanResponderCapture: () => true,
      onMoveShouldSetPanResponder: () => true,
      onMoveShouldSetPanResponderCapture: () => true,

      onPanResponderGrant: (evt, gestureState) => {
        if(evt.nativeEvent.locationX >= 0 && evt.nativeEvent.locationX <= chartWidth){
          swipeIndex = Math.floor((evt.nativeEvent.locationX / chartWidth) * data.length);
        }
        console.log(swipeIndex);
      },
      onPanResponderMove: (evt, gestureState) => {
        swipeIndex = Math.floor((evt.nativeEvent.locationX / chartWidth) * data.length);
        console.log(swipeIndex);
      },
      onPanResponderRelease: () => {
        return;
      },
      onPanResponderTerminate: () => {
        return;
      }
    })
  ).current;

  return (
    <View style={styles.chartContainer}>
      <View style={{flex: 1, textAlign: 'left'}}>
        <Text style={styles.cryptoSymbol}>{props.symbol}</Text>
        <Text style={styles.cryptoName}>{props.name}</Text>
      </View>

      <View style={{ height: 120, flexDirection: 'row', marginBottom: 15, marginTop: 15 }}>
          <View style={{ flex: 1, marginLeft: 10 }} {...panResponder.panHandlers} onLayout={(event) => {chartWidth = event.nativeEvent.layout.width;}}>
            <AreaChart
              style={{ flex:1, height: 120 }}
              data={props.data}
              contentInset={{ top: 10, bottom: 10 }}
              curve={shape.curveNatural}
              svg={{ fill: 'url(#gradient)' }}
              >
              <Tooltip/>
              <ChartLine/>
              <Gradient/>
            </AreaChart>
          </View>
      </View>

      <View style={{flex: 1, alignItems: 'center', marginTop: 20}}>
        <View style={styles.buttonsContainer}>
          <View style={styles.button}>
            <Text style={styles.buttonText} style={{color: '#ffffff'}}>
              1D
            </Text>
          </View>
          <View>
            <Text style={styles.buttonText}>
              1W
            </Text>
          </View>
          <View>
            <Text style={styles.buttonText}>
              1M
            </Text>
          </View>
          <View>
            <Text style={styles.buttonText}>
              1Y
            </Text>
          </View>
          <View>
            <Text style={styles.buttonText}>
              ALWAYS
            </Text>
          </View>
        </View>
      </View>
    </View>
  );
};


const styles = StyleSheet.create({
  container: {
    flex:1,
    backgroundColor: '#e9f0ff',
    alignItems: 'center',
    justifyContent: 'flex-start',
    paddingTop: 30
  },
  chartContainer: {
    backgroundColor: '#ffffff',
    width: '90%',
    borderRadius: 20,
    padding: 20,
    marginBottom: 20
  },
  cryptoSymbol: {
    fontFamily: 'Poppins_500Medium',
    fontSize: 25,
    color: '#000000'
  },
  cryptoName: {
    fontFamily: 'Poppins_500Medium',
    fontSize: 20,
    color: '#000000'
  },
  priceContainer: {
    flex: 1,
    alignItems: 'center'
  },
  currentPrice: {
    fontFamily: 'Poppins_500Medium',
    fontSize: 18,
    color: '#000000',
    margin: 0
  },
  text: {
    fontFamily: 'Poppins_600SemiBold',
    fontSize: 30,
    color: '#000000'
  },
  buttonsContainer: {
    width: '80%', 
    flexDirection: 'row', 
    justifyContent: 'space-between'
  },
  button: {
    paddingTop: 5,
    paddingBottom: 5,
    paddingLeft: 10,
    paddingRight: 10,
    backgroundColor: 'rgb(41, 114, 255)',
    borderRadius: 30
  },
  buttonText: {
    fontFamily: 'Poppins_500Medium',
    fontSize: 12,
    color: '#000000'
  }
});
