import 'react-native-gesture-handler';
import React from 'react';
import { StyleSheet, Text, View, Image,TouchableOpacity} from 'react-native';
import { useFonts, Poppins_300Light, Poppins_400Regular, Poppins_500Medium, Poppins_600SemiBold} from '@expo-google-fonts/poppins';
import { AppLoading } from 'expo';
import Icon from 'react-native-vector-icons/FontAwesome';


const onPress = (crypto) => {
  alert("qui si aprirà:"+crypto);
};

export default function Crypto(data) {
  let [fontsLoaded] = useFonts({
    Poppins_300Light,
    Poppins_400Regular,
    Poppins_500Medium,
    Poppins_600SemiBold
  });

  console.log("CRYPTO:",data)




  if (!fontsLoaded) {
    return <AppLoading/>;
  }
  else{
    return (
      <TouchableOpacity style={styles.cryptoContainer} onPress={()=>onPress(data["name"])} activeOpacity={0.8}>
        {data["name"]=="add"?<Icon name="plus" size={50} style={{color:'#4ddbff'}}/>:
        <Text style={styles.text}>{data["name"]}</Text>
        }
      </TouchableOpacity>
    );
  }
};

const styles = StyleSheet.create({
  cryptoContainer: {
    width:'99%',
    backgroundColor: '#111111',
    alignItems: 'center',
    justifyContent: 'center',
    height: 200,
    borderRadius: 10,
    marginTop: 10,
    marginBottom: 10,
    shadowColor: '#666',
       shadowOffset: { width: 2, height: 2 },
       shadowOpacity: 0.5,
       shadowRadius: 2,
       elevation: 2, 
  },
  plus: {
    width: 50,
    height: 50
  },
  text: {
    fontFamily: 'Poppins_500Medium',
    fontSize: 25,
    color: '#ffffff'
  },
});
