    _additionalOffset = 0;
    _releaseVal = 0;
    _releaseAnim = null;

    _currentEvent = null;
    _container = null;
    _spacerLayout = null;

    _size = [];
    _position = [];
    _order = [];

    constructor(props) {
        super(props);

        const { data } = this.props;
        for (let i = 0; i < data.length; i++) {
            this._order[i] = i;
        }

        this._panResponder = PanResponder.create({
            onStartShouldSetPanResponderCapture: (evt, {numberActiveTouches}) => {
                if (numberActiveTouches > 1) {
                    return true;
